workspace "calliope"  architecture "x64"    configurations  {    "Debug",    "Test",    "Release"  }    outputdir = "%{cfg.buildcfg}-%{cfg.system}-%{cfg.architecture}"project "calliope"  location "calliope"  kind "SharedLib"  language "C++"    targetdir ("bin/" .. outputdir .. "/%{prj.name}")  objdir ("bin-int/" .. outputdir .. "/%{prj.name}")    files  {    "%{prj.name}/src/**.hpp",    "%{prj.name}/src/**.cpp"  }    includedirs  {      }    calliopedir = "%{cfg.buildtarget.relpath}"    filter "system:windows"    cppdialect "C++20"    staticruntime "On"    systemversion "latest"        defines    {      "CLP_PLATFORM_WINDOWS",      "CLP_BUILD_DLL"    }      filter "configurations:Debug"    defines "CLP_DEBUG"    symbols "On"    filter "configurations:Test"    defines "CLP_TEST"    optimize "On"    filter "configurations:Release"    defines "CLP_RELEASE"    optimize "On"project "sandbox"  location "sandbox"  kind "ConsoleApp"  language "C++"    targetdir ("bin/" .. outputdir .. "/%{prj.name}")  objdir ("bin-int/" .. outputdir .. "/%{prj.name}")    files  {    "%{prj.name}/src/**.hpp",    "%{prj.name}/src/**.cpp"  }    includedirs  {    "calliope/src"  }    links  {    "calliope"  }    print(calliopedir)  postbuildcommands  {    {"{COPYFILE} ../bin/" .. outputdir .. "/calliope/calliope.dll ../bin/" .. outputdir .. "/sandbox" }  }    filter "system:windows"    cppdialect "C++20"    staticruntime "On"    systemversion "latest"        defines    {      "CLP_PLATFORM_WINDOWS"    }      filter "configurations:Debug"    defines "CLP_DEBUG"    symbols "On"    filter "configurations:Test"    defines "CLP_TEST"    optimize "On"    filter "configurations:Release"    defines "CLP_RELEASE"    optimize "On"